using System.Collections; using System.Collections.Generic; using UnityEngine;  public class PlayerController : MonoBehaviour {     //float speed = 0.5f;     public float moveSpeed = 5f;          public Rigidbody playerRigidbody;     public float jumpForce = 50.0f;      // Start is called before the first frame update     void Start()     {      }      // Update is called once per frame     void Update()     {       }     void FixedUpdate()     {                  if (StageRotation.MoveFlag == true)         {             //Move             float H = Input.GetAxis("LeftStickHorizontal");             float V = Input.GetAxis("LeftStickVertical");             if (H != 0 || V != 0)             {                 transform.Translate(new Vector3(H, 0, 0) * Time.deltaTime * moveSpeed, Space.World);             }             else             {                 //没有移动             }             //Jump             if (FootCollision.grounded == true)             {                 //ADownButton                 if (Input.GetButtonDown("ADownButton"))                 {                     print("ADownButton");                     playerRigidbody.AddForce(new Vector3(0f, jumpForce, 0f));                     FootCollision.grounded = false;                 }              }                       }               }  } 